ahem, this one works easily :)
class ['a] node = object(self:'a) val mutable nx =(Obj.magic 0 : 'a) method next = nx method set_next n = nx <- n method apply (f: 'a->unit) upto = f self; if self <> upto then self#next#apply f upto initializer nx <- self end;;
let a = new node;;
